import React from 'react'
import styled from 'styled-components'
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/effect-cards";
import "swiper/css/pagination";
import "swiper/css/navigation";



import { Pagination, Navigation, Autoplay, EffectCards } from "swiper";

import img1 from '../assets/NFT_Boxes/LeafyDayMementoStudioNFTBox.png';
import img2 from '../assets/NFT_Boxes/LeafyDayMementoStudioNFTBox.png';
import img3 from '../assets/NFT_Boxes/DesertCaveMementoStudioNFTBox.png';
import img4 from '../assets/NFT_Boxes/MiamiPalmMementoStudioNFTBox.png';
import img5 from '../assets/NFT_Boxes/RoseyMementoStudioNFTBox.png';

import Arrow from '../assets/Arrow.svg';


const Container = styled.div`
width: 25vw;
height: 70vh;

@media (max-width: 70em){
    height: 60vh;
}

@media (max-width: 64em){
    height: 50vh;
    width: 30vw;
}
@media (max-width: 48em){
    height: 50vh;
    width: 40vw;
}
@media (max-width: 30em){
    height: 45vh;
    width: 60vw;
}

.swiper{
    width: 100%;
    height: 100%;
}

.swiper-slide{
    background-color: ${props => props.theme.carouselColor};

    border-radius: 20px;

    display: flex;
    justify-content: center;
    align-items: center;

    img{
        display: block;
        width: 100%;
        height: auto;
        object-fit: cover;
    }
}

.swiper-button-next{
    color: ${props => props.theme.text};
    right: 0;
    width: 4rem;
    top: 60%;

    background-image: url(${Arrow});
    background-position: center;
    background-size: cover;

    &:after{
        display: none;
    }

    @media (max-width: 64em){
    width: 3rem;

    }
    @media (max-width: 30em){
    width: 2rem;

    }
}
.swiper-button-prev{
    color: ${props => props.theme.text};
    left: 0;
    top: 60%;
    width: 4rem;
    transform: rotate(180deg);
    background-image: url(${Arrow});
    background-position: center;
    background-size: cover;

    &:after{
        display: none;
    }
    @media (max-width: 64em){
    width: 3rem;

    }
    @media (max-width: 30em){
    width: 2rem;

    }


}

`

const Carousel = () => {
  return (
    <Container>
        <Swiper
        autoplay={{
            delay:2000,
            disableOnInteraction:false,
        }}
        pagination={{
            type:'fraction',
        }}
        scrollbar={{
            draggable:true
        }}
        modules={[EffectCards,Pagination, Navigation, Autoplay]}
        navigation={true}
        effect={"cards"}
        grabCursor={true}

        className="mySwiper"
      >
        <SwiperSlide>  <img width={500} height={400}  src={img1} alt="Memento Studio" />   </SwiperSlide>
        <SwiperSlide>  <img width={500} height={400}  src={img2} alt="Memento Studio" />   </SwiperSlide>
        <SwiperSlide>  <img width={500} height={400}  src={img3} alt="Memento Studio" />   </SwiperSlide>
        <SwiperSlide>  <img width={500} height={400}  src={img4} alt="Memento Studio" />   </SwiperSlide>
        <SwiperSlide>  <img width={500} height={400}  src={img5} alt="Memento Studio" />   </SwiperSlide>
      </Swiper>
    </Container>
  )
}

export default Carousel
